    private void doneActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_doneActionPerformed
        // TODO add your handling code here:
        String fName,lName,uName,uPss,Cnc,mail;
        String phoneNo,country,cty,Gndr,DateOfBirth;
        String path="d:\\Register.txt";
        File s=new File(path);
        if(s.exists())
        {
       //  System.out.println("file fond...........");
           
            try {
                FileWriter t=new FileWriter(s.getAbsolutePath(),true);
             //  BufferedWriter bw = new BufferedWriter(t);
                

                fName=Fnme.getText();
                lName=Lnme.getText();
                uName=Unme.getText();
                uPss=uPas.getText();
                Cnc=Cnic.getText();
                phoneNo=PhnNo.getText();
                country=cntry.getSelectedItem().toString();
                cty=city.getText();
                mail=Email.getText();
                Gndr=Gender.getSelectedItem().toString();
                DateOfBirth=Dtbrth.getText();
              //  Chck=Check.
                
                if(fName.equals("")&&lName.equals("")&&uName.equals("")&&uPss.equals("")&&Cnc.equals("")&&phoneNo.equals("")&&country.equals("")&&cty.equals("")&&DateOfBirth.equals("")&&Gndr.equals("")&&mail.equals(""))
                {
                    System.out.println("file fond...........");
                t.append("\n");
                t.write(fName);
                t.write(",");
                t.write(lName);
                t.write(",");
                t.write(uName);
                t.write(",");
                t.write(uPss);
                t.write(",");
                t.write(Cnc);
                t.write(",");
                t.write(phoneNo);
                t.write(",");
                t.write(country);
                t.write(",");
                t.write(cty);
                t.write(",");
                t.write(Gndr);
                t.write(",");
                t.write(DateOfBirth);
                t.write(mail);
                //t.write('\n');
                //t.write(chck);
                //bw.close();
                 t.close(); 
                 this.setVisible(false);
        rg d=new rg();
        d.setPreferredSize(new Dimension(400,500));
        d.setVisible(rootPaneCheckingEnabled);
                }
                else
                {
                    Err.setText("Plz,Fill all the Tabs");
                }
              
                
            } catch (IOException ex) {
                Logger.getLogger(Rgster.class.getName()).log(Level.SEVERE, null, ex);
            }
        }
    }//GEN-LAST:event_doneActionPerformed

